import React from 'react';

export default class RandomUserTable extends React.Component {

    state = {
        loading: true,
        person: null
    }
    async componentDidMount() {
        const url = "https://api.randomuser.me/";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({person: data.results[0], loading: false})
        console.log(data);

    }

    render() {
        return (
            <table id="dtBasicExample" class="table table-striped table-bordered table-sm" cellspacing="0" width="100%">
            <thead>
                <tr>
                    <th class="th-sm">Last Name</th>
                    <th class="th-sm">First Name</th>
                    <th class="th-sm">Email</th>
                    <th class="th-sm">Cell Phon</th>
                    <th class="th-sm">DOB</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                    <td>{this.state.person.name.last}</td>
                    <td>{this.state.person.name.first}</td>
                    <td>{this.state.person.email}</td>
                    <td>{this.state.person.cell}</td>
                    <td>{this.state.person.dob}</td>
                </tr>
            </tbody>
            </table>
        )
    }
}
